    private void jLabel2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel2MouseClicked
        // TODO add your handling code here:
        ArrayList<String> regiones = new ArrayList<>();
        ArrayList<Integer> conteoRegiones = new ArrayList<>();

        for (Pkemon pichu : Pokemon) {
            String region = pichu.getRegion();

            if (regiones.contains(region)) {
                int i = regiones.indexOf(region);
                conteoRegiones.set(i, conteoRegiones.get(i) + 1);

            } else {
                regiones.add(region);
                conteoRegiones.add(1);
            }
        }
        String j = "";
        
            for (int i = 0; i < regiones.size(); i++) {
                j += regiones.get(i) + ":" + conteoRegiones.get(i) + "\n";
            }
        

        JOptionPane.showMessageDialog(this, "Pokemones registrados : " + Pokemon.size() + "\n" + j);

    }//GEN-LAST:event_jLabel2MouseClicked

