    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        // TODO add your handling code here:

        Pkemon P = new Pkemon(Tj_Nombre.getText(), Tj_Tipo.getText(), Tj_Region.getText(), Integer.parseInt(Tj_Salud.getText()));
        Pokemon.add(P);
        JOptionPane.showMessageDialog(this, "Tu pokemon se ha registrado con extito!");
        Tj_Nombre.setText("");
        Tj_Tipo.setText("");
        Tj_Region.setText("");
        Tj_Salud.setText("");

    }//GEN-LAST:event_jButton1MouseClicked

    private void actualizar() {
        DefaultListModel m = (DefaultListModel) jList1.getModel();
        m.removeAllElements();
        for (Pkemon pkemon : Pokemon) {
            m.addElement(pkemon);
        }
        jList1.setModel(m);
    }


